function labels = extendCluster(X, labels, p, neighbors, C, eps, minPts)    % extend a cluster with label C from the seed p    % you can define this function recursively. or use a while loop with a queue.     labels(p) = C;    k = 1;        % YOUR CODE HERE    while k ~= (length(neighbors) + 1)        %get next point from the queque        temp = [1:2];        temp = neighbors(k,:);        for j = 1:length(labels)            if temp == X(j,:)                Pnext = j;            end        end        if labels(Pnext) == -1            labels(Pnext) = C;        end        if labels(Pnext) == 0            labels(Pnext) = C;            neighbors_of_Pnext = findNeighbors(X, Pnext, eps);            neighbors = vertcat(neighbors, neighbors_of_Pnext);                    end                       k = k + 1;    end        % END OF CODE  end