function neighbors = findNeighbors(X, p, eps)    % find all data points in X within eps of the current cendroid point p    % attention p is a number/ the index of the pth data point    [m, n] = size(X); % m is the number of data points, n is the number of features    neighbors = []; % initialize empty matrix    for i = 1:m      if norm(X(p,:)-X(i,:)) < eps        neighbors = [neighbors; X(i,:)]; % append datapoint to the matrix      end    end  end